AllTweets.map:
function (doc) {
  emit(doc._id, {text: doc.text, sentiment: doc.sentiment, coordinates: doc.location.coordinates, time: doc.timestamp, place: doc.place });
}

TweetsByState.map:
function (doc) {
  emit(doc.place.state, {text: doc.text, sentiment: doc.sentiment, coordinates: doc.location.coordinates, time: doc.timestamp, place: doc.place });
}

TweetsByPlace.map:
function (doc) {
  emit(doc.place.name, {text: doc.text, sentiment: doc.sentiment, coordinates: doc.location.coordinates, time: doc.timestamp, place: doc.place });
}

